package lcec.model;

import java.io.Serializable;
import java.util.Date;

public class PrimaryMDReportModel implements Serializable{

	private static final long serialVersionUID = 1L;
	
	public PrimaryMDReportModel() {
		// TODO Auto-generated constructor stub
	}
	/*
	 * 	This file contains two parts, --Primary MD Reprot and Referring MD Report
	 * 
	 * 
	 * */
	
	private String primaryMdReport;
	private Date lastReferral;
	private int noOfReferrals;
	private int noOfCancers;
	private int noOfProcedures;
	private int noOfResections;

	public String getPrimaryMdReport() {
		return primaryMdReport;
	}
	public void setPrimaryMdReport(String primaryMDReport) {
		this.primaryMdReport = primaryMDReport;
	}
	public Date getLastReferral() {
		return lastReferral;
	}
	public void setLastReferral(Date lastReferral) {
		this.lastReferral = lastReferral;
	}
	public int getNoOfReferrals() {
		return noOfReferrals;
	}
	public void setNoOfReferrals(int noOfReferrals) {
		this.noOfReferrals = noOfReferrals;
	}
	public int getNoOfCancers() {
		return noOfCancers;
	}
	public void setNoOfCancers(int noOfCancers) {
		this.noOfCancers = noOfCancers;
	}
	public int getNoOfProcedures() {
		return noOfProcedures;
	}
	public void setNoOfProcedures(int noOfProcedures) {
		this.noOfProcedures = noOfProcedures;
	}
	public int getNoOfResections() {
		return noOfResections;
	}
	public void setNoOfResections(int noOfResections) {
		this.noOfResections = noOfResections;
	}
	public static long getSerialversionuid() {
		return serialVersionUID;
	}
	
	// Referring MD Report part.
	private String referringMDReport;

	public String getReferringMDReport() {
		return referringMDReport;
	}
	public void setReferringMDReport(String referringMDReport) {
		this.referringMDReport = referringMDReport;
	}
	
}
